import React, { useState } from 'react';
import Button from '@material-ui/core/Button';
import AddIcon from '@material-ui/icons/Add';

const CreateNote = (props) =>{

    const [note, setNote] = useState({
        title: "",
        content: ""
    });

    const inputEvent = (event) => {

        const {name, value} = event.target;

        setNote((prevValue)=>{
            return {
                ...prevValue,
                [name]: value
            }
        });
    }

    const AddNote = () => {
        props.passNote();
    }

    return (
        <>
            <div className="main_div">
                <div className="center_div">
                    <form className="frmNote">
                    <br />                 
                        <div className="mb-3">
                            <input type="text" name="title" className="form-control" placeholder="Add title" onChange={inputEvent}/>
                        </div>
                        <div className="mb-3">
                            <textarea name="content" className="form-control" rows="3" placeholder="Add Note here" onChange={inputEvent}></textarea>
                        </div>
                        <Button className="newBtn" onClick={AddNote}>
                            <AddIcon/>
                        </Button>
                    </form>
                </div>
            </div>
        </>
    );
}

export default CreateNote;
